mapper.select:
        SELECT


--         级联表的字段

        FROM fangjian  a



                and a.id in

                #{item}



                and a.fangjian_name like CONCAT('%',#{params.fangjianName},'%')


                and a.fangjian_types = #{params.fangjianTypes}


                  and a.fangjian_money >= #{params.fangjianMoneyStart}   


                  and a.fangjian_money <= #{params.fangjianMoneyEnd}   


                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


                and a.fangjian_content like CONCAT('%',#{params.fangjianContent},'%')




        order by a.${params.orderBy} desc
